import axios from 'axios';
import React, { useState, useEffect } from 'react';
import styles from './DetailReview.module.css';
import { useParams } from 'react-router-dom';
import ReactStars from 'react-stars';

export default function DetailReview({ name }) {
  //리뷰 제출
  const [inputValue, setInputValue] = useState();

  const changeInputValue = (e) => {
    setInputValue(e.target.value);
  };
  const sendData = () => {
    axios
      .post('', {
        inputValue,
      })
      .then((response) => console.log(response.data))
      .catch((Error) => {
        console.log(Error.response.data);
      });
  };
  //리뷰 사진업로드
  const onUploadImage = useCallback((e) => {
    if (!e.target.files) {
      return;
    }

    const formData = new FormData();
    formData.append('image', e.target.files[0]);

    axios({
      baseURL: API_HOST,
      url: '/images/:username/thumbnail',
      method: 'POST',
      data: formData,
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
      .then((response) => {
        console.log(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const onUploadImageButtonClick = useCallback(() => {
    if (!inputRef.current) {
      return;
    }
    inputRef.current.click();
  }, []);

  // 해당식당의 리뷰출력
  const param = useParams();
  const [dataList, setDataList] = useState([]);
  const loadData = () => {
    const category = param.category;
    console.log('NEW', category);
    axios
      .get('http://3.34.2.11:8082/api/images')
      .then((response) => {
        const result = response.data.filter((item) => item.name === name);
        setDataList(result);
      })
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    loadData();
  }, [param.category]);

  return (
    <>
      <div className={styles.review_container}>
        <textarea
          onChange={changeInputValue}
          value={inputValue}
          className={styles.review_container2}
          spellCheck='false'
          placeholder='솔직한 리뷰를 남겨보세요.'
        ></textarea>
        <input
          type='file'
          accept='image/*'
          ref={inputRef}
          onChange={onUploadImage}
        />
        <button label='이미지 업로드' onClick={onUploadImageButtonClick} />
        <button className={styles.button} onClick={sendData}>
          리뷰 쓰기
        </button>
        <p className={styles.total}>최근 리뷰 ({dataList.length})</p>
        <section className={styles.section}>
          {dataList.map((dataList) => (
            <div className={styles.review_container3}>
              <p className={styles.id}>{dataList.userId}</p>
              <ReactStars
                count={5}
                value={dataList.rate}
                edit={false}
                size={20}
              />
              <img className={styles.img} src={dataList.image} alt='' />
              <p className={styles.contents}>{dataList.contents}</p>
            </div>
          ))}
        </section>
      </div>
    </>
  );
}
