  //       console.log(result);
  //     })
  //     .catch((error) => {
  //       console.log('요청실패');
  //       console.log(error);
  //     });
  // };

  return (
    <>
      <form>
        <label htmlFor='profile-upload' />
        <input
          type='file'
          id='profile-upload'
          accept='image/*'
          onChange={onChangeImg}
        />
      </form>
      {/* <button onClick={onClickLogin2}>제출</button> */}
      <button onClick={onPrint}>쩐다</button>
    </>
  );
}
